import {useState,  useEffect, useCallback} from 'react';

export const useOrder=()=>{
    const [item,setItems]=useState([]);
    const [totalAmount,setTotalAmount]=useState(null);
    const [totalQuantity,setTotalQuantity]=useState(null);
    const [status,setStatus]=useState(null);
    const [orderId,setOrderId]=useState(null);

    const createOrder=useCallback((items,totalQuantity,totalAmount,orderId)=>{
        setItems(items);
        setTotalAmount(totalAmount);
        setTotalQuantity(totalQuantity);
        setOrderId(orderId);
        setStatus('created');
    },[]);

    const updateOrder=useCallback((status)=>{
        setStatus(status);
    },[]);

    const clearContext=()=>{
        setItems([]);
        setTotalAmount(null);
        setTotalQuantity(null);
        setOrderId(null);
        setStatus(null);
    }

return {item, totalAmount, totalQuantity, status, orderId, createOrder, updateOrder, clearContext};


};
