//import { v4 as uuidv4 } from 'uuid';
import axios from "axios";
import {useState,useEffect,useRef,useContext} from 'react';
import {useDispatch,useSelector} from 'react-redux';
import {useNavigate, useParams} from 'react-router-dom';
//import classes from './FabricItemOrder.module.css';
import {cartActions} from '../Store/cart_slice';
import {uiActions} from '../Store/ui_slice';
import PaymentForm from '../components/PaymentForm';
import Card from '../components/card';
import {OrderContext} from '../Context/order-context';


const OrderPage=()=>{

    const {orderId}=useParams();
    console.log(orderId);
    const dispatch=useDispatch();
    const {item, totalQuantity, totalAmount}=useContext(OrderContext);
    console.log(item, totalQuantity, totalAmount);
    /*let display_items=[];
    display_items=item;
    console.log(display_items);*/
    const navigate=useNavigate();
    const cart=useSelector((state)=>state.cart);
    const [razorpayKey,setKey]=useState(null);
    const [order,setOrder]=useState(null);
useEffect(()=>{
    const checkOut=async()=>{
        try{
            const responseKey=await fetch('http://localhost:5000/getkey');
            if(!responseKey.ok)
                throw new Error("Payment failed");
            const {key}=await responseKey.json();
            setKey(key);
            const response=await fetch('http://localhost:5000/payment/checkout',{
                            method:'POST',
                            headers:{'content-type':'application/json'},
                            body:JSON.stringify({amount:totalAmount})
                            });
            if(!response.ok)
                throw new Error("Payment failed");
            const {order}=await response.json();
            //let {order}=data;
            console.log(order);
            setOrder(order);
            }catch(err){
               console.log(err.message);
               //navigate('/fabrics');
            }

    }
    checkOut();
},[]);
    const paymentHandler=async(formData)=>{
    var options = {
          key: razorpayKey,
          amount: order.amount,
          currency: "INR",
          name: "J'Adore",
          description: "Test Transaction",
          image: "https://example.com/your_logo",
          order_id: order.id,
          handler: async(response)=>{
            try{
                const responseData=await fetch('http://localhost:5000/payment/paymentverification',{
                            method:'POST',
                            headers:{'content-type':'application/json'},
                            body:JSON.stringify(response)
                });
                const {data}=await responseData.json();

    //update rzr_payment_id in Orders

                 const payment_response=await fetch('http://localhost:5000/orders',{
                            method:'PUT',
                            headers:{'content-type':'application/json'},
                            body:JSON.stringify({order_id:orderId,rzr_payment_id:data})
                });
        console.log("after paymment");

                if(payment_response){
    //delete from Cart
                        console.log(" deleting cart");

                const cart_id=JSON.parse(localStorage.getItem('cartId'))
                console.log(cart_id);
                const response=await fetch(
                        `http://localhost:5000/cart/${cart_id}`,
                        { method:'DELETE'}
                      );
                  if(!response)
                    console.log("error deleting cart");
                      localStorage.removeItem('cartId');
                    console.log("payment Id",data);
                    navigate('/paymentsuccess/'+data);
                }
            }catch(err){
                console.log(err);
            }
          },
          prefill: {
              "name": formData.first_name+" "+formData.last_name,
              "email": formData.email,
              "contact": formData.phone
          },
          notes: {
              "address": "J'Adore Corporate Office"
          },
          theme: {
              "color": "#8F0555"
          }
    };
     var rzp1 = new window.Razorpay(options);
     rzp1.open();
 };
return(
    <>
    {order !== null && razorpayKey !== null  && (
    <div style={{ display: 'flex' }}>
        <PaymentForm onSubmit={paymentHandler}/>
        {item.length!==0 &&
        <Card style={{ display: 'flex', flexDirection: 'column' }} >
        {item.map((itm)=>(
        <>
            <div style={{ display: 'flex', alignItems: 'bottom' }}>
                <span><img src={itm.image} style={{ width: '50px', height: '50px' }}  alt="" /></span>
                <span>{itm.type}</span>
                <span>Rs. {itm.rate?.toFixed(2)}</span>
                <span>X {itm.quantity}</span>
                <span>Rs. {(itm.quantity*itm.rate)?.toFixed(2)}</span>
            </div></>
            ))}
            <div style={{marginTop:'auto', textAlign:'right'}}><span></span>
                <span>No. of items: {totalQuantity}</span>
                <span></span>
                <span>Amount: Rs {totalAmount?.toFixed(2)}</span>
            </div>
        </Card>}
    </div>
    )}
    </>
    );
};
export default OrderPage;
