import {useContext} from 'react';
import classes from './Drawer.module.css';
import { NavLink } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

import { AuthContext } from '../Context/auth-context';

const Drawer = ({ isOpen, onClose }) => {
const {isAdmin,logout}=useContext(AuthContext);
    const navigate=useNavigate();

const logOut=()=>{
    logout();
    navigate('/homepage');
}
  return (
    <div className={`${classes.drawer} ${isOpen ? classes.open : ''}`}>
    {isAdmin?(
     <div className={classes.drawerContent}>
             <button onClick={onClose} className={classes.closeButton}>X</button>
             <nav>
               <ul>
                 <li>
                   <NavLink to="/profile" activeClassName={classes.active}>Profile</NavLink>
                 </li>
                 <li>
                   <NavLink to="/fabrics/new/fabrics" activeClassName={classes.active}>Add Items</NavLink>
                 </li>
                 <li>
                   <NavLink to="/support" activeClassName={classes.active}>Support</NavLink>
                 </li>
                 <li>
                   <button onClick={onClose} className={classes.logoutButton}>Logout</button>
                 </li>
               </ul>
             </nav>
           </div>
       ):
       (
       <div className={classes.drawerContent}>
            <button onClick={onClose} className={classes.closeButton}>X</button>
            <nav>
              <ul>
                <li>
                  <NavLink to="/userProfile" activeClassName={classes.active}>Profile Settings</NavLink>
                </li>
                <li>
                  <NavLink to="/clientOrders" activeClassName={classes.active}>Orders</NavLink>
                </li>
                <li>
                  <NavLink to="/support" activeClassName={classes.active}>Support</NavLink>
                </li>
                <li>
                  <button onClick={logOut()} className={classes.logoutButton}>Logout</button>
                </li>
              </ul>
            </nav>
          </div>
          )}
    </div>
  );
};

export default Drawer;
