import classes from './FabricsList.module.css';
import {useContext} from 'react';
import {useDispatch} from 'react-redux';
import {NavLink} from 'react-router-dom';
import  '@fortawesome/fontawesome-free/css/all.css';
import {AuthContext} from '../Context/auth-context';


function FabricsList({ list }) {
const dispatch=useDispatch();
const authCtx=useContext(AuthContext);

 const removeEntryHandler = (id) => {
 alert("Deleting item "+id)
    const deleteFabric = async(dispatch)=>{
      try{
           const response=await fetch(
                    `http://localhost:5000/fabric/${id}`,
                    { method:'DELETE',
                      headers:{Authorization:'Bearer'+ authCtx.token}
                    }
                  );

            if(! response.ok)
                throw new Error("Could not delete the Fabric");
        }
        catch(err){
            console.log(err);
            dispatch(
                uiActions.showNotification({
                status: 'error',
                title:  'Error!',
                message: error.message,
              })
            );        }
        }
      deleteFabric();
   }

   const addFabricHandler = () => {
   }
let category="We are currently Out of Stock";
const items= Object.keys(list).map(id => {
category=list[id].category+" Store";
  return {id:list[id]._id, name:list[id].type+" "+list[id].sub_category+" "+list[id].category, desc:list[id].desc ,details:list[id].details,image:list[id].image, colour:list[id].colour};
console.log(items);
});
  return (
<div>

    <h2>{category} </h2>
     {authCtx.isAdmin && <div style={{width:"80%", textAlign:"left"}}><button onClick={addFabricHandler}>Add Fabric</button></div>}

<ul>
      {items.map((item) => (
    <div className={classes.responsive}>
      <div className={classes.gallery}>

      <li key={item.id}>
        <NavLink to={{pathname:`/fabrics/${item.id}` }}  className={({isActive})=>isActive?classes.active:undefined}end>
            <img src={item.image} alt="Cinque Terre"/>


        <div className={classes.desc}>{item.name} {item.colour}</div>
        </NavLink>
        {authCtx.isAdmin && <div><i class="fa-regular fa-trash-can" onClick={()=>removeEntryHandler(item.id)}></i></div>}
      </li>
      </div>
    </div>
  ))
}</ul>
</div>
);
}
export default FabricsList;
