import {useState} from 'react';
import AuthForm from '../components/AuthForm';
import {json, redirect, useParams} from'react-router-dom';
import {AuthContext} from '../Context/auth-context';
import {useContext, useEffect} from'react';
import { useAuth } from '../hooks/auth-hook';
//import {authenticate} from '../util/auth';

const AuthenticationPage=()=>{
const authCtx=useContext(AuthContext);
const [errors, setErrors]=useState(null);
    let {mode,order}=useParams();

//let mode='login';
    //useEffect(()=>{
        const searchParams = new URL(window.location.href).searchParams;
        //console.log(searchParams.get('mode'));
         mode = searchParams.get('mode') ;
         if (!(mode === 'login' || mode === 'signup' || mode==='logout')) {
             //throw json({ message: 'Unsupported mode.' }, { status: 422 });
             setErrors([...errors, 'Invalid login mode']);
             return;
          }
        if(mode==='logout'){
            authCtx.logout();
        }
        redirect('/');
    //});




    const getData=async(formData)=>{

     if (mode==='signup' && (formData.password !== formData['confirm_password']))
                 setErrors([...errors, 'Password and Confirm Password not matching']);

        //throw json({ message: 'Password and Confirm Password not matching' }, { status: 422 });

     const authData={
        email: formData.email,
        password: formData.password,
     };
console.log(authData);
     if(mode==='signup'){
        authData.firstname= formData.first_name;
        authData.lastname= formData.last_name;
     }
     //console.log("local cart id"+localStorage.getItem('cartId'));
     const cart_id=localStorage.getItem('cartId');
     if(cart_id)
        authData.cart_id= cart_id;
    else
        authData.cart_id=null;
console.log(authData);
     const response= await fetch('http://localhost:5000/user/'+ mode, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(authData)
     });
  console.log(response);
     /* if (response.status === 422 || response.status === 401) {
         return response;
       }*/

       if (!response.ok) {
       //console.log(response);
       try{
         const errorData = await response.json();
         console.error('Error:', errorData.message);
         }catch(e){
           setErrors([...errors, e.message]);
         }
          setErrors([...errors, errorData.message]);
         // Handle the error message (e.g., display it to the user)
        // throw json({message:errorData.message},{status:500});

    }
       const resData= await response.json();
console.log("json response ",resData);
       const token=resData.token;
       const userid=resData.userId;
       const expiration=new Date();
       expiration.setHours(expiration.getHours()+1);
console.log(token, userid, expiration);
       authCtx.login(userid, token, expiration);

        if(order==='open')
           return redirect('/payment');
        return redirect('/');
}
return <AuthForm onSubmit={getData}/>;
}
export default AuthenticationPage;
