import {useState,useEffect,useContext,useCallback} from 'react';
import {useDispatch} from 'react-redux';
import {useNavigate, useParams} from 'react-router-dom';
import {cartActions} from '../Store/cart_slice';
import PaymentForm from '../components/PaymentForm';
import Card from '../components/card';
import {OrderContext} from '../Context/order-context';


const OrderPage=()=>{

    const {orderId}=useParams();
    console.log(orderId);
    const dispatch=useDispatch();
    const {status,item, totalQuantity, totalAmount, updateStatus}=useContext(OrderContext);
    console.log(item, totalQuantity, totalAmount);
    /*let display_items=[];
    display_items=item;
    console.log(display_items);*/
    const navigate=useNavigate();
    //const cart=useSelector((state)=>state.cart);
    const [razorpayKey,setKey]=useState(null);
    const [order,setOrder]=useState(null);

useEffect(()=>{
    if(!status || status!=='created')
        navigate('/items');
    //console.log(razorpayKey);
    //if(razorpayKey) return;

    const checkOut=async()=>{
        let created_order=null;
        try{
            const responseKey=await fetch('http://localhost:5000/getkey');
            if(!responseKey.ok)
                throw new Error("Payment failed");
            const {key}=await responseKey.json();
            setKey(key);
            const response=await fetch('http://localhost:5000/payment/checkout',{
                            method:'POST',
                            headers:{'content-type':'application/json'},
                            body:JSON.stringify({amount:totalAmount})
                            });
            //console.log(response);
            if(!response.ok)
                throw new Error("Payment failed");
            let {created_order}=await response.json();
            //let {order}=data;
            console.log(created_order);
            if(created_order){
                setOrder(created_order);
               // console.log(order);
            }
        }catch(err){
            updateStatus('failed');
            //console.log(err.message);
            navigate('/items');
        }

    }
    checkOut();
},[]);



    const paymentHandler=async(formData)=>{
    var options = {
          key: razorpayKey,
          amount: order.amount,
          currency: "INR",
          name: "J'Adore",
          description: "Test Transaction",
          image: "https://example.com/your_logo",
          order_id: order.id,
          handler: async(response)=>{
          const data=null;
            try{
                const responseData=await fetch('http://localhost:5000/payment/paymentverification',{
                            method:'POST',
                            headers:{'content-type':'application/json'},
                            body:JSON.stringify(response)
                });
                let {data}=await responseData.json();

                //console.log(data);
                if(data){
                console.log("updating orders table");
                     const payment_response=await fetch('http://localhost:5000/orders',{
                                method:'PUT',
                                headers:{'content-type':'application/json'},
                                body:JSON.stringify({id:orderId,order_id:order.id,rzr_payment_id:data,customer_data:formData,status:order.status})
                    });

            console.log(payment_response);

        /*update stocks of products
        console.log(item);
        for (const product of item) {
        const stock_response=await fetch('http://localhost:5000/store',{
                                method:'PUT',
                                headers:{'content-type':'application/json'},
                                body:JSON.stringify({id:product.id,quantity:product.quantity})
                    });

            console.log(stock_response);
         };*/
        //delete from Cart once order is placed

                const cart_id=JSON.parse(localStorage.getItem('cartId'))
                if(cart_id){
                    const response=await fetch(
                            `http://localhost:5000/cart/${cart_id}`,
                            { method:'DELETE'}
                          );
                      //set cart_id to null in user table
                    try{
                        const user=JSON.parse(localStorage.getItem('userData'))
                        console.log(user);
                        if(user){
                            const user_id=user.userId;

                            if(user_id){
                                console.log("user: ",user_id);
                                const response=await fetch(
                                    'http://localhost:5000/user',
                                {
                                    method: 'PUT',
                                    headers:{'content-type':'application/json'},
                                    body:JSON.stringify({
                                      id:user_id,
                                      cart_id:null})
                                })


                                 if (!response){
                                    throw new Error('Updating User failed');
                                }

                            }
                          }
                      }catch(error){
                        console.log(error.message);
                      }
                    }

                  localStorage.removeItem('cartId');
                  dispatch(cartActions.emptyCart());

                //add vendor id and order details in vendor table
                    await fetch('http://localhost:5000/vendor',{
                                method:'POST',
                                headers:{'content-type':'application/json'},
                                body:JSON.stringify({vendor_id:item.created_by, item_id:item.id, order_id:orderId, quantity:item.quantity, status:item.status})
                            });

                    updateStatus('created');
                    //console.log(order.id,data);
                    navigate('/paymentsuccess/'+order.id+'/'+data);
                }
               }catch(error){
                    console.log(error.message);
               }


          },
          prefill: {
              "name": formData.name,
              "email": formData.email,
              "contact": formData.phone
          },
          notes: {
              "address": "J'Adore Corporate Office"
          },
          theme: {
              "color": "#8F0555"
          }
    };
     var rzp1 = new window.Razorpay(options);
     rzp1.open();
 };
return(
    <>
    {order !== null && razorpayKey !== null  && (
    <div style={{ display: 'flex',width:'100%' }}>
        <PaymentForm onSubmit={paymentHandler}/>
        {item.length!==0 &&
        <Card style={{ display: 'flex', flexDirection: 'column' }} >
        {item.map((itm)=>(
        <>
            <div style={{ display: 'flex', alignItems: 'bottom' }}>
                <span><img src={itm.image} style={{ width: '50px', height: '50px' }}  alt="" /></span>
                <span>{itm.type}</span>
                <span>Rs. {itm.rate?.toFixed(2)}</span>
                <span>X {itm.quantity}</span>
                <span>Rs. {Number(itm.quantity*itm.rate)?.toFixed(2)}</span>
            </div></>
            ))}
            <div style={{marginTop:'auto', textAlign:'right'}}><span></span>
                <span>No. of items: {totalQuantity}</span>
                <span></span>
                <span>Amount: Rs {totalAmount?.toFixed(2)}</span>
            </div>
        </Card>}
    </div>
    )}
    </>
    );
};
export default OrderPage;
