import { useEffect, useState} from 'react';
import {useParams} from 'react-router-dom';
import {useDispatch} from'react-redux';
import {uiActions } from '../Store/ui_slice';
import FabricsList from '../components/FabricsList';

function FabricsPage() {
console.log("hello");
  const [fabrics, setFabrics] = useState([]);
  const dispatch= useDispatch();
  //const {category} = useParams();
  //console.log(category);
    const fetchFabrics=async(dispatch)=>{
        try{
        console.log("in fabrics useeffect");

              const response = await fetch('http://localhost:5000/fabrics');
              if (!response.ok) {
                throw new Error('Fetching Fabrics failed')
              }
              const resData = await response.json();
              console.log(resData);
              setFabrics(resData);
          }catch(error){
              dispatch(
                uiActions.showNotification({
                status: 'error',
                title:  'Error!',
                message: error.message,
              })
            );
           }
        }

  useEffect(() => {
        fetchFabrics();
        return()=>{
            dispatch(uiActions.clearNotification());
        }
    },[dispatch]);
    return (
    <>{fabrics && <FabricsList list={fabrics} updateFabricList={fetchFabrics}/>}</>);
  };

    export default FabricsPage;
