import AuthForm from '../components/AuthForm';
import {json, redirect} from'react-router-dom';
import {AuthContext} from '../Context/auth-context';

const AuthenticationPage=()=>{
return <AuthForm/>;
}
export default AuthenticationPage;

export async function action({request}){

    const auth=useContext(AuthContext);
    const searchParams=new URL(request.url).searchParams;
    const mode=searchParams.get('mode')|| 'login';

    if (mode !== 'login' && mode !== 'signup') {
        throw json({ message: 'Unsupported mode.' }, { status: 422 });
     }
     const order=searchParams.get('order');

     const data = await request.formData();

     if (data.get('password') !== data.get("confirm_password"))
        throw json({ message: 'Password and Confirm Password not matching' }, { status: 422 });

     const authData={
        email: data.get('email'),
        password: data.get('password'),
     };

     if(mode==='signup'){
        authData.firstName= data.get('first_name')
        authData.lastName= data.get('last_name');
     }
     console.log("local cart id"+localStorage.getItem('cartId'));

     if(localStorage.getItem('cartId'))
        authData.cart_id= data.get('cartId');
console.log(authData);
     const response= await fetch('http://localhost:5000/user/'+ mode, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(authData)
     });

      if (response.status === 422 || response.status === 401) {
         return response;
       }

       if (!response.ok) {
         throw json({ message: 'Could not authenticate user.' }, { status: 500 });
       }
       const resData= await response.json();
       const token=resData.token;
       const userid=resData.userId;
       //localStorage.setItem('token', token);
       const expiration=new Date();
       expiration.setHours(expiration.getHours()+1);
       //localStorage.setItem('expiration',expiration.toISOString);

       if(mode=='signup'){
           auth.login(userid, token, expiration);
       }
        if(order==='open')
           return redirect('/payment');
        return redirect('/');
}
