import {useEffect, useState, useContext} from 'react';
import { AuthContext } from '../Context/auth-context';
import classes from './MainNavigation.module.css';
import {NavLink} from 'react-router-dom';
import CartButton from '../components/CartButton';

function MainNavigation() {
    const [showLink,setShowlink]=useState("Login/Sign Up");
    const [mode,setMode]=useState("login");
    const authCtx=useContext(AuthContext);
console.log("logged ",AuthCtx.isLoggedIn);
    useEffect(()=>{
        if(AuthContext.isLoggedIn){
            setShowlink("logout");
            setMode("logout");
        }
    },[AuthContext.isLoggedIn,showLink,mode]);
  return (
    <header className={classes.header}>
      <div className="topnav">
            <NavLink to="/items" className={({isActive})=>isActive?classes.active:undefined}end><h1>J'Adore</h1></NavLink>
      </div>
       <div className="topnav-right">
                <NavLink to={`/user?mode=${mode}`} className={({isActive})=>isActive?classes.active:undefined}end>{showLink}</NavLink>

               <NavLink to="/" className={({isActive})=>isActive?classes.active:undefined}end>Wishlist</NavLink>

               <NavLink className={({isActive})=>isActive?classes.active:undefined}end>Cart<CartButton/></NavLink>

      </div>
    </header>
  );
}

export default MainNavigation;
