import {useRef, useState,useEffect} from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import classes from './CartItem.module.css';
import { cartActions } from '../Store/cart_slice';

const CartItem = (props) => {
    const inputQuantity=useRef();
    const totalPriceRef=useRef();
    const dispatch = useDispatch();
    const navigate=useNavigate();
    const {id, type, rate, image, quantity, totalPrice } = props.item;
    const [changedQuantity,setChangedQuantity]=useState(quantity);
    const [changedPrice,setChangedPrice]=useState(quantity);


     useEffect(()=>{
     console.log("rerender");
        inputQuantity.current.value=changedQuantity;
        totalPriceRef.current.value=changedPrice;
    },[changedQuantity,changedPrice]);

  const updateCart=()=>{
    //e.value=changedQuantity;
    console.log(changedQuantity)
    dispatch(cartActions.changeCartItem({id,quantity:changedQuantity}));
  }

  const changeQuantity=(e)=>{
    //console.log(e.target.value);
    setChangedQuantity(inputQuantity.current.value)
    e.value=changedQuantity;
    setChangedPrice(inputQuantity.current.value*rate);
    updateCart();
    }

  const removeItemHandler = () => {
    //dispatch(cartActions.removeFromCart(id));
    //navigate('/Cart');
    inputQuantity.current.value-=0.5;
  };

  const addItemHandler = () => {
    /*dispatch(
      cartActions.addToCart({item:{
        id,
        type,
        rate,
        image,
        quantity:0.5 }})
    );*/

  };

  return (
    <li  className={classes.item}>
        <div className={classes.itemdet}>
            <div>
                <img src={image}/>
            </div>
            <div>
                 <h3>{type}</h3>
                  <div className={classes.itemprice}>(Rs.{rate}/meter)</div>
            </div>

            <div className={classes.quantity}>
                  x <span><input type="text"  onBlur={changeQuantity} ref={inputQuantity} /></span>
            </div>
            <div ref ={totalPriceRef} className={classes.price}>
                  Rs.{totalPrice}
            </div>
        </div>

        <div className={classes.actions}>
              <button onClick={removeItemHandler}>-</button>
              <button onClick={addItemHandler}>+</button>
        </div>
    </li>
  );
};

export default CartItem;
