import { Suspense} from 'react';
import {useRouteLoaderData,json,defer,Await,redirect} from 'react-router-dom';
import classes from './FabricItem.module.css';
import FabricItemOrder from './FabricItemOrder';
import FabricItem from './FabricItem';
import FabricsList from './FabricsList';

function FabricDetailPage(){
const {fabric,fabrics}=useRouteLoaderData('fabric-detail');
  return (
     <>
     <Suspense fallback={<p style={{ textAlign: 'center'}}>Loading...</p>}>
         <Await resolve={fabric}>
         {(loadedFabric)=> <FabricItem item={loadedFabric}/>}
         </Await>
     </Suspense>
     <Suspense fallback={<p style={{ textAlign: 'center'}}>Loading...</p>}>
          <Await resolve={fabrics}>
          {(loadedFabrics)=> <FabricsList list={loadedFabrics}/>}
          </Await>
      </Suspense>
     </>
  );
}

export default FabricDetailPage;

async function loadFabric(id){
    const response=await fetch('http://localhost:5000/fabrics/'+id);
    if(! response.ok){
        throw json(
            {message:'Could not load selected fabric'},
            {status:500}
        );
    }else{
        const resData=await response.json();
        return resData;
    }
}
async function loadFabrics(){
    const response=await fetch('http://localhost:5000/fabrics');
    if(! response.ok){
        throw json(
            {message:'Could not load fabrics'},
            {status:500}
        );
    }else{
        const resData=await response.json();
        return resData;
    }
}


export async function loader({params,request}){
    const id=params.fabricId;
    return defer({
        fabric:await loadFabric(id),
        fabrics:loadFabrics(),
    });
}

export async function action({params,request}){
    const id=params.fabricId;

    const response=await fetch('http://localhost:5000/fabrics/'+id,{
    method:request.method,
    });

    if(!response.ok){
        throw json(
        {message:'Could not delete fabric from list'},
        {status:500}
        );
    }
    redirect('/fabrics');
}
